// Add scroll event listener for animation triggers
const handleScroll = () => {
  const scrollY = window.scrollY;
  const windowHeight = window.innerHeight;

  // Get all sections that should animate on scroll
  const animateSections = [
    'mission_content',
    'how_it_works_content',
    'who_we_help_content',
    'testimonials_content',
    'contact_content'
  ];

  // For each section, check if it's in view and add animation class
  animateSections.forEach(sectionId => {
    const section = document.getElementById(sectionId);
    if (section) {
      const sectionTop = section.getBoundingClientRect().top;
      const sectionVisible = sectionTop < windowHeight - 100;

      if (sectionVisible) {
        section.style.opacity = '1';
        section.style.transform = 'translateY(0)';
      }
    }
  });
};

// Initialize sections for scroll animations
const initScrollAnimations = () => {
  const animateSections = [
    'mission_content',
    'how_it_works_content',
    'who_we_help_content',
    'testimonials_content',
    'contact_content'
  ];

  animateSections.forEach(sectionId => {
    const section = document.getElementById(sectionId);
    if (section) {
      section.style.opacity = '0';
      section.style.transform = 'translateY(30px)';
      section.style.transition = 'opacity 0.8s ease, transform 0.8s ease';
    }
  });

  // Trigger initial check
  handleScroll();
};

// Add scroll event listener
window.addEventListener('scroll', handleScroll);

// Initialize animations
initScrollAnimations();

